             // configure general system 1
                jq(function() {

                       getField('SG1.value').prop("disabled", true);
                        // jq('#ts1').hide();
                       getField("SO1.value").change( function(){

                            var val = htmlForm.getValueIfLegal('SO1.value'); 

                              if(val == 136443){

                                       //jq('#ts1').show();
                                    getField('SG1.value').prop("disabled", false);
                              }else {
                                      //jq('#ts1').hide();
                                       setValue('SG1.value', null);
                                       getField('SG1.value').prop("disabled", true);
                              }

                          });

                    });




