             var showDetails = function(){

                var val = htmlForm.getValueIfLegal('check.value');
                    if(val){
                         jq('#details').show();
                    }else{
                        jq('#details').hide();
                    }
              }

           jq(function() {
                   jq('#details').hide();
                   getField('check.value').change(showDetails) ;
                    jq('#hide1').hide();
                    jq('#hide2').hide();

             });


            //togle past cancer 
           var showPastCancer = function(){
                var val = htmlForm.getValueIfLegal('pastCancerCheck.value');
                    if(val){
                         jq('#pastCancer').show();
                    }else{
                        jq('#pastCancer').hide();
                    }
              }

               jq(function() {
                   jq('#pastCancer').hide();
                   getField('pastCancerCheck.value').change(showPastCancer) 

             });



           //togle hiv
           var showhiv = function(){
               var val = htmlForm.getValueIfLegal('hivcheck.value');                   
                    if(val == 703){

                          jq('#hivpos').show();
                           jq('#hivneg').hide();
                    }else if (val == 664){

                        jq('#hivneg').show();
                        jq('#hivpos').hide();
                    }else {
                            jq('#hivpos').hide();
                            jq('#hivneg').hide();
                         }
              }

               jq(function() {
                    jq('#hivpos').hide();
                    jq('#hivneg').hide();
                   getField('hivcheck.value').change(showhiv) 


             });




           //togle on art
           var showart = function(){
               var val = htmlForm.getValueIfLegal('checkart.value');                   
                    if(val == 1065){

                          jq('#yesart').show();
                           jq('#noart').hide();
                    }else if (val == 1066){

                        jq('#noart').show();
                        jq('#yesart').hide();
                        jq('#yeswitch').hide();
                    }else {
                            jq('#yesart').hide();
                            jq('#noart').hide();
                         }
              }

               jq(function() {
                    jq('#yesart').hide();
                    jq('#noart').hide();
                   getField('checkart.value').change(showart) 


             });





           //togle art switch
           var showswitch = function(){
               var val = htmlForm.getValueIfLegal('checkswitch.value');                   
                    if(val ){

                          jq('#yeswitch').show();
                    }else {

                        jq('#yeswitch').hide();
                    }
                }

               jq(function() {
                     jq('#yeswitch').hide();
                     getField('checkswitch.value').change(showswitch) 


             });



                 //togle on ctx
           var showctx = function(){
               var val = htmlForm.getValueIfLegal('checkctx.value');                   
                    if(val == 1065){

                          jq('#ctx').show();
                    }else {
                        jq('#ctx').hide();
                    }
              }

               jq(function() {
                    jq('#ctx').hide();
                   getField('checkctx.value').change(showctx) 


             });   


            // adding more Presenting Complaints Dynamically
         jq(function(){
          jq('#1-removeEntry').remove();
           jq('#10-addEntry').remove();
           jq('#1-toggleContainer').show();

        });


    jq(function(){
       jq('button.addEntry').click(function(){
       var correctedAddButtonId = parseFloat(this.id) + 1;
      var contentAddId = "#" + correctedAddButtonId + "-toggleContainer";
      jq(contentAddId).toggle(true);
       jq('#' + this.id).toggle(false);  
      jq('#' + parseFloat(this.id) + '-removeEntry').toggle(false);
      return false;});
    });

     jq(function(){
       jq('button.removeEntry').click(function(){
      var correctedRemoveButtonId = parseFloat(this.id) - 1;
      var contentAddId = "#" + parseFloat(this.id) + "-toggleContainer";
      jq(contentAddId).toggle(false);
      jq( ':input:not(:button)', contentAddId).val([]);
      jq('#' + correctedRemoveButtonId + '-addEntry').toggle(true); 
      jq('#' + correctedRemoveButtonId + '-removeEntry').toggle(true);

      return false;
         });

    });


