// setting null button behaviour  for Constittional

                   jq(function() {

                        jq("#n1").change( function(){

                          if(jq("#n1").prop("checked") == true){

                                      jq("#SMP_A1").prop("disabled", true);
                                      jq("#SMP_A1").prop("checked", false);
                                      setValue('weightloss.value', 1115);

                                       jq("#SMP_A2").prop("disabled", true);
                                        jq("#SMP_A2").prop("checked", false);
                                        setValue('weightgain.value', null);

                                        jq("#SMP_A3").prop("disabled", true);
                                        jq("#SMP_A3").prop("checked", false);
                                        setValue('fevers.value', null);


                                       jq("#SMP_A4").prop("disabled", true);
                                        jq("#SMP_A4").prop("checked", false);
                                        setValue('A4.value', null);

                                        jq("#SMP_A5").prop("disabled", true);
                                        jq("#SMP_A5").prop("checked", false);
                                        setValue('A5.value', null);
                            } 
                            else if(jq("#n1").prop("checked") == false){
                                      jq("#SMP_A1").prop("disabled", false);

                                       jq("#SMP_A2").prop("disabled", false);

                                        jq("#SMP_A3").prop("disabled", false);

                                        jq("#SMP_A4").prop("disabled", false);

                                        jq("#SMP_A5").prop("disabled", false);


                                    }
                          });


                     });



