  var showDetails = function(){
     var val = htmlForm.getValueIfLegal('check.value');
         if(val){
              jq('#details').show();
         }else{
             jq('#details').hide();
         }
   }

jq(function() {
        jq('#details').hide();
        getField('check.value').change(showDetails) ;
         jq('#hide1').hide();
         jq('#hide2').hide();

  });


